function todoCtrl (){

    var $ctrl = this;

    $ctrl.$onInit = function () {
        $ctrl.add = function (a, b) {
          var result = a + b;
          return result;
        };

        $ctrl.and = function (a, b) {
          var result = a && b;
          return result;
        };

        $ctrl.or = function (a, b) {
          var result = a || b;
          return result;
        };
    }

}
